<script>
    document.addEventListener('DOMContentLoaded', function() {
        const hamburger = document.createElement('div');
        hamburger.className = 'hamburger-menu';
        hamburger.innerHTML = '<span></span><span></span><span></span>';

        const overlay = document.createElement('div');
        overlay.className = 'menu-overlay';

        const mobilePedidos = document.createElement('div');
        mobilePedidos.className = 'mobile-pedidos';
        mobilePedidos.innerHTML = '<a href="/" class="pedidosb">Pedidos</a>';

        document.body.appendChild(overlay);

        const cartContainer = document.querySelector('.pedidos.flex_r');
        cartContainer.insertBefore(hamburger, cartContainer.firstChild);

        const menu = document.getElementById('menu');
        menu.insertBefore(mobilePedidos, menu.firstChild);

        hamburger.addEventListener('click', function() {
            menu.classList.toggle('active');
            hamburger.classList.toggle('active');
            overlay.classList.toggle('active');
        });

        overlay.addEventListener('click', function() {
            menu.classList.remove('active');
            hamburger.classList.remove('active');
            overlay.classList.remove('active');
        });
    });
</script>
